    {
        _projectService = sampleAppService;
    }

    [HttpGet]
    public async Task<List<ProjectDto>> GetAllAsync()
    {
        return await _projectService.GetAllAsync();
    }


    [HttpPost]    
    public async Task<ProjectDto> Create(ProjectDto projectDto)
